import "./App.css";
import React, { useState, useRef } from "react";

function App() {
  const tableModalRef = useRef({});

  const save = (grade) => {
    if (tableModalRef.current) {
      tableModalRef.current.calScore(grade);
    }
  };

  // 학년 별로 행 개수를 저장하기 위한 state변수 선언
  const [grade1Rows, setGrade1Rows] = useState(0);
  const [grade2Rows, setGrade2Rows] = useState(0);
  const [grade3Rows, setGrade3Rows] = useState(0);

  // 추가 버튼 클릭 시 호출
  const addRow = (grade) => {
    if (grade === 1) {
      setGrade1Rows((prevRows) => prevRows + 1);
    } else if (grade === 2) {
      setGrade2Rows((prevRows) => prevRows + 1);
    } else {
      setGrade3Rows((prevRows) => prevRows + 1);
    }
  };

  return (
    <div className="App">
      <div className="gr grade1">
        <h1>1학년</h1>
        <button onClick={() => addRow(1)}>추가</button>
        <button>삭제</button>
        <button onClick={() => save(1)}>저장</button>
        <TableModal
          numRows={grade1Rows}
          tableModalRef={tableModalRef}
          addRow={() => addRow(1)}
        ></TableModal>
      </div>

      <div className="gr grade2">
        <h1>2학년</h1>
        <button onClick={() => addRow(2)}>추가</button>
        <button>삭제</button>
        <button>저장</button>
        <TableModal
          numRows={grade2Rows}
          tableModalRef={tableModalRef}
          addRow={() => addRow(2)}
        ></TableModal>
      </div>

      <div className="gr grade3">
        <h1>3학년</h1>
        <button onClick={() => addRow(3)}>추가</button>
        <button>삭제</button>
        <button>저장</button>
        <TableModal
          numRows={grade3Rows}
          tableModalRef={tableModalRef}
          addRow={() => addRow(3)}
        ></TableModal>
      </div>
    </div>
  );
}

function TableModal({ numRows, tableModalRef }) {
  // 상태 값 저장을 위한 state변수 선언
  const [subTitles, setSubTitles] = useState(
    Array(numRows)
      .fill("")
      .map(() => "")
  );
  const [credits, setCredits] = useState(
    Array(numRows)
      .fill("")
      .map(() => "")
  );
  const [attScores, setAttScores] = useState(
    Array(numRows)
      .fill("")
      .map(() => "")
  );
  const [assScores, setAssScores] = useState(
    Array(numRows)
      .fill("")
      .map(() => "")
  );
  const [midScores, setMidScores] = useState(
    Array(numRows)
      .fill("")
      .map(() => "")
  );
  const [finScores, setFinScores] = useState(
    Array(numRows)
      .fill("")
      .map(() => "")
  );

  const calScore = (grade) => {
    let sum_Credit = 0;
    // let sum_AttScore = 0;
    // let sum_AssScore = 0;
    // let sum_Midterm = 0;
    // let sum_Final = 0;

    console.log(grade);

    if (grade === 1) {
      sum_Credit = credits.reduce((acc, curr) => {
        const parsed = parseInt(curr) || 0;
        return parsed > 1 ? acc + parsed : acc;
      }, 0);
    } else if (grade === 2) {
      sum_Credit = credits.reduce((acc, curr) => {
        const parsed = parseInt(curr) || 0;
        return parsed > 1 ? acc + parsed : acc;
      }, 0);
    } else if (grade === 3) {
      sum_Credit = credits.reduce((acc, curr) => {
        const parsed = parseInt(curr) || 0;
        return parsed > 1 ? acc + parsed : acc;
      }, 0);
    }
    return sum_Credit;
  };
  tableModalRef.current.calScore = calScore;

  // 입력 값 변화시 호출
  const onInputChange = (e, i, type) => {
    // input 태그의 입력된 값을 추출
    const { value } = e.target;

    // 과목이름
    if (type === "subTitle") {
      if (subTitles.includes(value)) {
        alert("이미 등록된 과목입니다 다른 과목을 등록해주세요.");
      } else {
        const newSubTitles = [...subTitles];
        newSubTitles[i] = value;
        setSubTitles(newSubTitles);
      }

      //학점
    } else if (type === "credit") {
      const newCredits = [...credits];
      newCredits[i] = value;
      setCredits(newCredits);

      // 출석점수
    } else if (type === "attScore") {
      if (value < 0 || value > 20) {
        alert("출석 점수는 0 ~ 20점 사이의 숫자만 입력해주세요.");
      } else {
        // 조건에 맞으면 현재 해당 값을 상태로 설정
        const newAttScores = [...attScores];
        newAttScores[i] = value;
        setAttScores(newAttScores);
      }

      // 과제점수
    } else if (type === "assScore") {
      if (value < 0 || value > 20) {
        alert("과제 점수는 0 ~ 20점 사이의 숫자만 입력해주세요.");
      } else {
        const newAssScores = [...assScores];
        newAssScores[i] = value;
        setAssScores(newAssScores);
      }

      //중간고사
    } else if (type === "midterm") {
      if (value < 0 || value > 30) {
        alert("중간고사 점수는 0 ~ 30점 사이의 숫자만 입력해주세요.");
      } else {
        const newMidScores = [...midScores];
        newMidScores[i] = value;
        setMidScores(newMidScores);
      }

      //기말고사
    } else if (type === "final") {
      if (value < 0 || value > 30) {
        alert("기말고사 점수는 0 ~ 30점 사이의 숫자만 입력해주세요.");
      } else {
        const newFinScores = [...finScores];
        newFinScores[i] = value;
        setFinScores(newFinScores);
      }
    }
  };

  const tableRows = [];

  for (let i = 0; i < numRows; i++) {
    tableRows.push(
      <tr key={i}>
        <td>
          <select>
            <option>교양</option>
            <option>전공</option>
          </select>
        </td>
        <td>
          <select>
            <option>필수</option>
            <option>선택</option>
          </select>
        </td>
        <td>
          <input
            type="text"
            name="subTitle"
            onChange={(e) => onInputChange(e, i, "subTitle")}
          />
        </td>
        <td>
          <input
            type="text"
            name="credit"
            onChange={(e) => onInputChange(e, i, "credit")}
            style={{ width: 45 + "px" }}
          />
        </td>
        <td>
          <input
            type="text"
            name="attScore"
            onChange={(e) => onInputChange(e, i, "attScore")}
            style={{ width: 45 + "px" }}
          />
        </td>
        <td>
          <input
            type="text"
            name="assScore"
            onChange={(e) => onInputChange(e, i, "assScore")}
            style={{ width: 45 + "px" }}
          />
        </td>
        <td>
          <input
            type="text"
            name="midterm"
            onChange={(e) => onInputChange(e, i, "midterm")}
            style={{ width: 45 + "px" }}
          />
        </td>
        <td>
          <input
            type="text"
            name="final"
            onChange={(e) => onInputChange(e, i, "final")}
            style={{ width: 45 + "px" }}
          />
        </td>
        <td className="subTotalScore"></td>
        <td></td>
        <td className="grade"></td>
      </tr>
    );
  }

  let footerContent;
  if (numRows > 0) {
    footerContent = (
      <tr>
        <td colSpan={3}>합계</td>
        <td className="sumCredit">{calScore.sum_Credit}</td>
        <td className="sumAttScore"></td>
        <td className="sumAssScore"></td>
        <td className="sumMidterm"></td>
        <td className="sumFinal"></td>
        <td className="totalScore"></td>
        <td className="avg"></td>
        <td className="finalGrade"></td>
      </tr>
    );
  }

  return (
    <table width={1000} border={1}>
      <thead style={{ backgroundColor: "#00796B", color: "#ffffff" }}>
        <tr>
          <th>이수</th>
          <th>필수</th>
          <th>과목명</th>
          <th>학점</th>
          <th>출석점수</th>
          <th>과제점수</th>
          <th>중간고사</th>
          <th>기말고사</th>
          <th>총점</th>
          <th>평균</th>
          <th>성적</th>
        </tr>
      </thead>
      <tbody>{tableRows}</tbody>
      <tfoot>{footerContent}</tfoot>
    </table>
  );
}

export default App;
