import React, { useState, useRef, useMemo } from "react";

function calculateGrade(score) {
  if (score >= 92) return "A+";
  if (score >= 91) return "A0";
  if (score >= 85) return "B+";
  if (score >= 80) return "B0";
  if (score >= 75) return "C+";
  if (score >= 70) return "C0";
  if (score >= 65) return "D+";
  if (score >= 60) return "D0";
  return "F";
}

function sortRows(a, b) {
  if (a.courseCategory !== b.courseCategory)
    return a.courseCategory.localeCompare(b.courseCategory);
  if (a.requirement !== b.requirement)
    return a.requirement.localeCompare(b.requirement);
  return a.courseName.localeCompare(b.courseName);
}

function TheTable({ gradeLevel }) {
  const rowID = useRef(0);
  const [rows, setRows] = useState([]);
  const [selectedRowsForDelete, setSelectedRowsForDelete] = useState([]);

  const validRows = useMemo(
    () => rows.filter((row) => row.grade !== "" && row.credits !== 1),
    [rows]
  );

  const addRow = () => {
    setRows((prevRows) => [
      ...prevRows,
      {
        id: rowID.current,
        courseCategory: "교양",
        requirement: "필수",
        courseName: "",
        credits: 0,
        attendanceScore: 0,
        assignmentScore: 0,
        midtermScore: 0,
        finalScore: 0,
        totalScore: 0,
        grade: "",
      },
    ]);
    rowID.current++;
  };

  const deleteRows = () => {
    if (selectedRowsForDelete.length === 0) {
      alert("삭제할 행을 선택해주세요.");
      return;
    }
    setRows((prevRows) =>
      prevRows.filter((row) => !selectedRowsForDelete.includes(row.id))
    );
    setSelectedRowsForDelete([]);
  };

  const saveRows = () => {
    const incompleteRows = rows.filter((row) => row.grade === "");
    if (incompleteRows.some((row) => row.courseName === "")) {
      alert("과목명을 입력해주세요.");
      return;
    }
    if (incompleteRows.some((row) => row.credits === 0)) {
      alert("학점을 입력해주세요.");
      return;
    }

    setRows((prevRows) =>
      prevRows.map((row) => {
        if (row.grade === "") {
          row.totalScore =
            row.attendanceScore +
            row.assignmentScore +
            row.midtermScore +
            row.finalScore;
          if (row.credits === 1) {
            row.grade = "P";
          } else {
            row.grade = calculateGrade(row.totalScore);
          }
        }
        return row;
      })
    );
    setRows((prevRows) => [...prevRows].sort(sortRows));
  };

  const toggleDeleteRow = (id) => {
    setSelectedRowsForDelete((prevSelectedRows) =>
      prevSelectedRows.includes(id)
        ? prevSelectedRows.filter((rowID) => rowID !== id)
        : [...prevSelectedRows, id]
    );
  };

  const updateRowValue = (index, key, value) => {
    setRows((prevRows) =>
      prevRows.map((row, i) =>
        i === index
          ? {
              ...row,
              [key]: key === "credits" || key.includes("Score") ? Number(value) : value,
            }
          : row
      )
    );
  };

  return (
    <div>
      <h1>{gradeLevel}학년</h1>
      <button onClick={addRow}>추가</button>
      <button onClick={deleteRows}>삭제</button>
      <button onClick={saveRows}>저장</button>

      <table width={1000} border={1}>
        <thead style={{ backgroundColor: "#00796B", color: "#ffffff" }}>
          <tr>
            <th>이수</th>
            <th>필수</th>
            <th>과목명</th>
            <th>학점</th>
            <th>출석점수</th>
            <th>과제점수</th>
            <th>중간고사</th>
            <th>기말고사</th>
            <th>총점</th>
            <th>평균</th>
            <th>성적</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row, index) => (
            <tr
              key={row.id}
              className={selectedRowsForDelete.includes(row.id) ? "delete" : ""}
              onClick={() => toggleDeleteRow(row.id)}
            >
              <td>
                <select
                  value={row.courseCategory}
                  onChange={(e) =>
                    updateRowValue(index, "courseCategory", e.target.value)
                  }
                >
                  <option value="교양">교양</option>
                  <option value="전공">전공</option>
                </select>
              </td>
              <td>
                <select
                  value={row.requirement}
                  onChange={(e) =>
                    updateRowValue(index, "requirement", e.target.value)
                  }
                >
                  <option value="필수">필수</option>
                  <option value="선택">선택</option>
                </select>
              </td>
              <td>
                <input
                  type="text"
                  value={row.courseName}
                  onChange={(e) =>
                    updateRowValue(index, "courseName", e.target.value)
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.credits}
                  onChange={(e) =>
                    updateRowValue(index, "credits", e.target.value)
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.attendanceScore}
                  onChange={(e) =>
                    updateRowValue(index, "attendanceScore", e.target.value)
                  }
                  disabled={row.credits === 1}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.assignmentScore}
                  onChange={(e) =>
                    updateRowValue(index, "assignmentScore", e.target.value)
                  }
                  disabled={row.credits === 1}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.midtermScore}
                  onChange={(e) =>
                    updateRowValue(index, "midtermScore", e.target.value)
                  }
                  disabled={row.credits === 1}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.finalScore}
                  onChange={(e) =>
                    updateRowValue(index, "finalScore", e.target.value)
                  }
                  disabled={row.credits === 1}
                />
              </td>
              <td>{row.totalScore}</td>
              <td>
                {validRows.length > 0
                  ? (
                      validRows.reduce((sum, row) => sum + row.totalScore, 0) /
                      validRows.length
                    ).toFixed(1)
                  : 0}
              </td>
              <td style={{ color: row.grade === "F" ? "red" : "black" }}>
                {row.grade}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TheTable;
