import React, { useState } from "react";

function TheTable({ grade }) {
  const [rows, setRows] = useState([]);

  // 행 추가
  function handleAddRow() {
    setRows((rows) => [
      ...rows,
      {
        course: "교양",
        type: "필수",
        subTitle: "",
        credit: 0,
        attScore: 0,
        assScore: 0,
        midterm: 0,
        final: 0,
        subTotalScore: 0,
        avg: 0,
        grade: "",
      },
    ]);
  }

  // set 이수
  function handleSetCourse(event, index) {
    const { value } = event.target;

    setRows((rows) =>
      rows.map((row, _index) =>
        _index === index ? { ...row, course: value } : row
      )
    );
  }

  // set 필수 or 전공
  function handleSetType(event, index) {
    const { value } = event.target;

    setRows((rows) =>
      rows.map((row, _index) =>
        _index === index ? { ...row, type: value } : row
      )
    );
  }

  // set 과목명
  function handleSetSubTitle(event, index) {
    const { value } = event.target;

    setRows((rows) =>
      rows.map((row, _index) =>
        _index === index ? { ...row, subTitle: value } : row
      )
    );
  }

  // set 학점
  function handleSetCredit(event, index) {
    let { value } = event.target;
    value = Number(value) || 0;

    setRows((rows) =>
      rows.map((row, _index) =>
        _index === index ? { ...row, credit: value } : row
      )
    );
  }

  // set 출석점수
  function handleSetAttScore(event, index) {
    let { value } = event.target;
    value = Number(value) || 0;

    if (value < 0 || value > 20) {
      alert("출석 점수는 0 ~ 20점 사이의 숫자만 입력해주세요.");
      return;
    }

    setRows((rows) =>
      rows.map((row, _index) =>
        _index === index ? { ...row, attScore: value } : row
      )
    );
  }

  // set 과제점수
  function handleSetAssScore(event, index) {
    let { value } = event.target;
    value = Number(value) || 0;

    if (value < 0 || value > 20) {
      alert("과제 점수는 0 ~ 20점 사이의 숫자만 입력해주세요.");
      return;
    }

    setRows((rows) =>
      rows.map((row, _index) =>
        _index === index ? { ...row, assScore: value } : row
      )
    );
  }

  // set 중간고사
  function handleSetMidterm(event, index) {
    let { value } = event.target;
    value = Number(value) || 0;

    if (value < 0 || value > 30) {
      alert("중간고사 점수는 0 ~ 30점 사이의 숫자만 입력해주세요.");
      return;
    }

    setRows((rows) =>
      rows.map((row, _index) =>
        _index === index ? { ...row, midterm: value } : row
      )
    );
  }

  // set 기말고사
  function handleSetFinal(event, index) {
    let { value } = event.target;
    value = Number(value) || 0;

    if (value < 0 || value > 30) {
      alert("기말고사 점수는 0 ~ 30점 사이의 숫자만 입력해주세요.");
      return;
    }

    setRows((rows) =>
      rows.map((row, _index) =>
        _index === index ? { ...row, final: value } : row
      )
    );
  }

  return (
    <div className="gr grade1">
      <h1>{grade}학년</h1>
      <button onClick={handleAddRow}>추가</button>
      <button>삭제</button>
      <button>저장</button>

      <table width={1000} border={1}>
        <thead style={{ backgroundColor: "#00796B", color: "#ffffff" }}>
          <tr>
            <th>이수</th>
            <th>필수</th>
            <th>과목명</th>
            <th>학점</th>
            <th>출석점수</th>
            <th>과제점수</th>
            <th>중간고사</th>
            <th>기말고사</th>
            <th>총점</th>
            <th>평균</th>
            <th>성적</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row, index) => (
            <tr key={index}>
              <td>
                <select
                  value={row.course}
                  onChange={(event) => handleSetCourse(event, index)}
                >
                  <option defaultValue="교양">교양</option>
                  <option defaultValue="전공">전공</option>
                </select>
              </td>
              <td>
                <select
                  value={row.type}
                  onChange={(event) => handleSetType(event, index)}
                >
                  <option defaultValue="필수">필수</option>
                  <option defaultValue="선택">선택</option>
                </select>
              </td>
              <td>
                <input
                  type="text"
                  name="subTitle"
                  value={row.subTitle}
                  onInput={(event) => handleSetSubTitle(event, index)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="credit"
                  value={row.credit}
                  style={{ width: 45 + "px" }}
                  onInput={(event) => handleSetCredit(event, index)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="attScore"
                  value={row.attScore}
                  style={{ width: 45 + "px" }}
                  onInput={(event) => handleSetAttScore(event, index)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="assScore"
                  value={row.assScore}
                  style={{ width: 45 + "px" }}
                  onInput={(event) => handleSetAssScore(event, index)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="midterm"
                  value={row.midterm}
                  style={{ width: 45 + "px" }}
                  onInput={(event) => handleSetMidterm(event, index)}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="final"
                  value={row.final}
                  style={{ width: 45 + "px" }}
                  onInput={(event) => handleSetFinal(event, index)}
                />
              </td>
              <td className="subTotalScore">{row.subTotalScore}</td>
              <td>{row.avg}</td>
              <td className="grade">{row.grade}</td>
            </tr>
          ))}
        </tbody>
        <tfoot>
          {rows.length > 0 ? (
            <tr>
              <td colSpan={3}>합계</td>
              <td className="sumCredit"></td>
              <td className="sumAttScore"></td>
              <td className="sumAssScore"></td>
              <td className="sumMidterm"></td>
              <td className="sumFinal"></td>
              <td className="totalScore"></td>
              <td className="avg"></td>
              <td className="finalGrade"></td>
            </tr>
          ) : null}
        </tfoot>
      </table>

      {/* <TableModal
          numRows={grade1Rows}
          tableModalRef={tableModalRef}
          addRow={() => addRow(1)}
        ></TableModal> */}
    </div>
  );
}

export default TheTable;
