import React, { useState } from 'react';
import './App.css';


function App() {

  // 학년 별로 행 개수를 저장하기 위한 state변수 선언
  const [grades, setGrades] = useState({
    grade1: [],
    grade2: [],
    grade3: []
  });

  // 추가 버튼 클릭 시 호출
  const addRow = (grade) => {
    setGrades((prevGrades) => {
      const updatedGrades = { ...prevGrades };
      updatedGrades[`grade${grade}`] = [...updatedGrades[`grade${grade}`], {}];
      return updatedGrades;
    });
  };

  const calculateSum = (grade) => {
    setGrades((prevGrades) => {
      const updatedGrades = { ...prevGrades };
      let sumAttScore = 0;
      let sumAssScore = 0;

      updatedGrades[`grade${grade}`].forEach((data) => {
        sumAttScore += data.attendanceScore || 0;
        sumAssScore += data.assignmentScore || 0;
      });

      updatedGrades[`grade${grade}`].sumAttScore = sumAttScore;
      updatedGrades[`grade${grade}`].sumAssScore = sumAssScore;

      return updatedGrades;
    });
  };

  return (
    <div className="App">
      <div className='gr grade1'>
        <h1>1학년</h1>
        <button onClick={()=> addRow(1)}>추가</button>
        <button>삭제</button>
        <button onClick={()=> calculateSum(1)}>저장</button>
        <TableModal numRows={grades.grade1.length} gradeData={grades.grade1}></TableModal>
      </div>

      <div className='gr grade2'>
        <h1>2학년</h1>
        <button onClick={()=>addRow(2)}>추가</button>
        <button>삭제</button>
        <button onClick={()=> calculateSum(2)}>저장</button>
        <TableModal numRows={grades.grade2.length} gradeData={grades.grade2}></TableModal>
      </div>

      <div className='gr grade3'>
        <h1>3학년</h1>
        <button onClick={()=>addRow(3)}>추가</button>
        <button>삭제</button>
        <button onClick={()=> calculateSum(3)}>저장</button>
        <TableModal numRows={grades.grade3.length} gradeData={grades.grade3}></TableModal>
      </div>
    </div>
  );
}

