import "./App.css";
import React, { useState, useRef } from "react";
import TableModal from "./TableModal";

function App() {
  const tableModalRef = useRef({});

  const save = (grade) => {
    if (tableModalRef.current) {
      tableModalRef.current.calScore(grade);
    }
  };

  // 학년 별로 행 개수를 저장하기 위한 state변수 선언
  const [grade1Rows, setGrade1Rows] = useState(0);
  const [grade2Rows, setGrade2Rows] = useState(0);
  const [grade3Rows, setGrade3Rows] = useState(0);

  // 추가 버튼 클릭 시 호출
  const addRow = (grade) => {
    if (grade === 1) {
      setGrade1Rows((prevRows) => prevRows + 1);
    } else if (grade === 2) {
      setGrade2Rows((prevRows) => prevRows + 1);
    } else {
      setGrade3Rows((prevRows) => prevRows + 1);
    }
  };

  return (
    <div className="App">
      <div className="gr grade1">
        <h1>1학년</h1>
        <button onClick={() => addRow(1)}>추가</button>
        <button>삭제</button>
        <button onClick={() => save(1)}>저장</button>
        <TableModal
          numRows={grade1Rows}
          tableModalRef={tableModalRef}
          addRow={() => addRow(1)}
        ></TableModal>
      </div>

      <div className="gr grade2">
        <h1>2학년</h1>
        <button onClick={() => addRow(2)}>추가</button>
        <button>삭제</button>
        <button onClick={() => save(2)}>저장</button>
        <TableModal
          numRows={grade2Rows}
          tableModalRef={tableModalRef}
          addRow={() => addRow(2)}
        ></TableModal>
      </div>

      <div className="gr grade3">
        <h1>3학년</h1>
        <button onClick={() => addRow(3)}>추가</button>
        <button>삭제</button>
        <button onClick={() => save(3)}>저장</button>
        <TableModal
          numRows={grade3Rows}
          tableModalRef={tableModalRef}
          addRow={() => addRow(3)}
        ></TableModal>
      </div>
    </div>
  );
}

export default App;
