import './App.css';
import React, { useState } from 'react';

function App() {

  // 학년 별로 행 개수를 저장하기 위한 state변수 선언
  const [ grade1Rows, setGrade1Rows ] = useState(0);
  const [ grade2Rows, setGrade2Rows ] = useState(0);
  const [ grade3Rows, setGrade3Rows ] = useState(0);

  // 추가 버튼 클릭 시 호출
  const addRow = (grade) => {
    if (grade === 1) {
      setGrade1Rows(prevRows => prevRows + 1);
    } else if(grade === 2) {
      setGrade2Rows(prevRows => prevRows + 1);
    } else {
      setGrade3Rows(prevRows => prevRows + 1);
    }
  };

  return (
    <div className="App">
      <div className='gr grade1'>
        <h1>1학년</h1>
        <button onClick={()=>addRow(1)}>추가</button>
        <button>삭제</button>
        <button>저장</button>
        <TableModal numRows={grade1Rows} addRow={()=> addRow(1)}></TableModal>
      </div>

      <div className='gr grade2'>
        <h1>2학년</h1>
        <button onClick={()=>addRow(2)}>추가</button>
        <button>삭제</button>
        <button>저장</button>
        <TableModal numRows={grade2Rows} addRow={()=> addRow(2)}></TableModal>
      </div>

      <div className='gr grade3'>
        <h1>3학년</h1>
        <button onClick={()=>addRow(3)}>추가</button>
        <button>삭제</button>
        <button>저장</button>
        <TableModal numRows={grade3Rows} addRow={()=> addRow(3)}></TableModal>
      </div>
    </div>
  );
}

function TableModal({ numRows }) {

  // 상태 값 저장을 위한 state변수 선언
  const [attScores, setAttScores] = useState(Array(numRows).fill(''));
  const [assScores, setAssScores] = useState(Array(numRows).fill(''));

  // 입력 값 변화시 호출
  const onInputChange = (e, index, type) => {

    // input 태그의 입력된 값을 추출하여 저장
    const { value } = e.target;

    // 출석점수인 경우
    if (type === 'attScore') {

      // 입력 값이 조건에 맞지 않으면 알림창을 띄워주고 해당 입력값을 공백으로 설정하여 올바른 점수로 다시 입력할 수 있도록 설정
      if (value < 0 || value > 30) {
        alert('출석 점수는 0 ~ 30점 사이의 숫자만 입력해주세요.');
      } else {

        // 조건에 맞으면 현재 해당 값을 상태로 설정
        const newAttScores = [...attScores]
        newAttScores[index] = value;
        setAttScores(newAttScores);
      }

      // 과제점수인 경우 (위 주석과 동일한 구조)
    } else if (type === 'assScore') {
      if (value < 0 || value > 30) {
        alert('과제 점수는 0 ~ 30점 사이의 숫자만 입력해주세요.');
      } else {

        const newAssScores = [...assScores]
        newAssScores[index] = value;
        setAssScores(newAssScores);
      }
    }

  };

  const tableRows = [];

  for (let i = 0; i < numRows; i++) {
    tableRows.push(
      <tr key={i}>
        <td>
          <select>
            <option>교양</option>
            <option>전공</option>
          </select>
        </td>
        <td>
          <select>
            <option>필수</option>
            <option>선택</option>
          </select>
        </td>
        <td>
          <input type='text' name='subName' />
        </td>
        <td>
          <input type='text' name='credit' style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='attScore' value={attScores[i]} onChange={(e)=> onInputChange(e, i, 'attScore')} style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='assScore' value={assScores[i]} onChange={(e)=> onInputChange(e, i, 'assScore')} style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='midterm' style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='final' style={{ width: 45 + 'px' }} />
        </td>
        <td className='subTotalScore'></td>
        <td></td>
        <td className='grade'></td>
      </tr>
    );
  }

  let footerContent;
  if (numRows > 0) {
    footerContent = (
      <tr>
        <td colSpan={3}>합계</td>
        <td className='sumCredit'></td>
        <td className='sumAttScore'></td>
        <td className='sumAssScore'></td>
        <td className='sumMidterm'></td>
        <td className='sumFinal'></td>
        <td className='totalScore'></td>
        <td className='avg'></td>
        <td className='finalGrade'></td>
      </tr>
    )
  }

