import './App.css';
import React, { useState } from 'react';

function App() {

  // 학년 별로 행 개수를 저장하기 위한 state변수 선언
  const [ grade1Rows, setGrade1Rows ] = useState(0);
  const [ grade2Rows, setGrade2Rows ] = useState(0);
  const [ grade3Rows, setGrade3Rows ] = useState(0);

  const [totalScores, setTotalScores] = useState({
    grade1: {attScore: 0, assScore: 0, midScore: 0, finScore: 0},
    grade2: {attScore: 0, assScore: 0, midScore: 0, finScore: 0},
    grade3: {attScore: 0, assScore: 0, midScore: 0, finScore: 0}
  });

  // 추가 버튼 클릭 시 호출
  const addRow = (grade) => {
    if (grade === 1) {
      setGrade1Rows(prevRows => prevRows + 1);
    } else if(grade === 2) {
      setGrade2Rows(prevRows => prevRows + 1);
    } else {
      setGrade3Rows(prevRows => prevRows + 1);
    }
  };

  const calScore = (grade) => {
    const tableRows = document.querySelectorAll(`.gr.grade${grade} input[type='text']`);
    let attScore = 0, assScore = 0, midScore = 0, finScore = 0;

    tableRows.forEach((input) => {
      const {name, value} = input;

      if (name === 'attScore') {
        attScore += Number(value || 0);
      } else if (name === 'assScore') {
        assScore += Number(value || 0);
      } else if (name === 'midterm') {
        midScore += Number(value || 0);
      } else {
        finScore += Number(value || 0);
      }
    }); 
    return {attScore, assScore, midScore, finScore};
  };

  const saveClickEvent = (grade) => {
    const calculatedScore = calScore(grade);
    setTotalScores((prevTotal) => ({...prevTotal, [`grade${grade}`]: calculatedScore}));

    return calculatedScore;
  }

  useEffect(() => {
    const updateScores = (grade, rows) => {
      const calculatedScore = calScore(grade);
      const gradeKey = `grade${grade}`;

      // 해당 학년의 행 개수와 totalScores의 학년 정보 개수를 비교하여 업데이트할 개수 결정
      const diff = rows - (totalScores[gradeKey] ? totalScores[gradeKey].length : 0);

      if (diff > 0) {
        setTotalScores((prevTotal) => ({
          ...prevTotal,
          [gradeKey]: [...(prevTotal[gradeKey] || []), ...Array(diff).fill({ attScore: 0, assScore: 0, midScore: 0, finScore: 0 })]
        }));
      } else if (diff < 0) {
        setTotalScores((prevTotal) => ({
          ...prevTotal,
          [gradeKey]: prevTotal[gradeKey].slice(0, rows)
        }));
      }

      // 이후 추가적인 로직을 수행할 수 있습니다.
      // attScore, assScore 상태 업데이트 등
    };

    updateScores(1, grade1Rows); // 1학년 점수 업데이트
    updateScores(2, grade2Rows); // 2학년 점수 업데이트
    updateScores(3, grade3Rows); // 3학년 점수 업데이트

  }, [grade1Rows, grade2Rows, grade3Rows, totalScores]);

  return (
    <div className="App">
      <div className='gr grade1'>
        <h1>1학년</h1>
        <button onClick={()=>addRow(1)}>추가</button> 
        <button>삭제</button>
        <button onClick={()=>saveClickEvent(1)}>저장</button>
        <TableModal numRows={grade1Rows} totalScores={totalScores.grade1} saveClickEvent={()=> saveClickEvent(1)} addRow={()=> addRow(1)}></TableModal>


      </div>

      <div className='gr grade2'>
        <h1>2학년</h1>
        <button onClick={()=>addRow(2)}>추가</button>
        <button>삭제</button>
        <button onClick={()=>saveClickEvent(2)}>저장</button>
        <TableModal numRows={grade2Rows} totalScores={totalScores.grade2} saveClickEvent={()=> saveClickEvent(2)} addRow={()=> addRow(2)}></TableModal>
      </div>

      <div className='gr grade3'>
        <h1>3학년</h1>
        <button onClick={()=>addRow(3)}>추가</button>
        <button>삭제</button>
        <button onClick={()=>saveClickEvent(3)}>저장</button>
        <TableModal numRows={grade3Rows} totalScores={totalScores.grade3} saveClickEvent={()=> saveClickEvent(3)} addRow={()=> addRow(3)}></TableModal>
      </div>
    </div>
  );
}

function TableModal({ numRows, saveClickEvent, totalScores}) {

  // 상태 값 저장을 위한 state변수 선언
  const [attScore, setAttScore] = useState(Array(numRows).fill('0'));
  const [assScore, setAssScore] = useState(Array(numRows).fill('0'));

  // 입력 값 변화시 호출
  const onInputChange = (e, rowIndex) => {

    // input 태그의 name값과 입력된 값을 추출하여 저장
    const { name, value } = e.target;

    // 출석점수인 경우
    if (name === 'attScore') {

      // 입력 값이 조건에 맞지 않으면 알림창을 띄워주고 해당 입력값을 공백으로 설정하여 올바른 점수로 다시 입력할 수 있도록 설정
      if (value < 0 || value >= 30) {
        alert('출석 점수는 0보다 크고 30보다 작아야 합니다.');
      } else {
        // 조건에 맞으면 현재 해당 값을 상태로 설정
        const newAttScores = [...attScore];
        newAttScores[rowIndex] = value;
        setAttScore(newAttScores);
      }

      // 과제점수인 경우 (위 주석과 동일한 구조)
    } else if (name === 'assScore') {
      if (value < 0 || value >= 30) {
        alert('과제 점수는 0보다 크고 30보다 작아야 합니다.');
      } else {
        const newAssScores = [...assScore];
        newAssScores[rowIndex] = value;
        setAssScore(newAssScores);
      }
    }

  };

  const tableRows = [];

  for (let i = 0; i < numRows; i++) {
    tableRows.push(
      <tr key={i}>
        <td>
          <select>
            <option>교양</option>
            <option>전공</option>
          </select>
        </td>
        <td>
          <select>
            <option>필수</option>
            <option>선택</option>
          </select>
        </td>
        <td>
          <input type='text' name='subName' />
        </td>
        <td>
          <input type='text' name='credit' style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='attScore' value={attScore[i]} onChange={(e) => onInputChange(e, i)} style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='assScore' value={assScore[i]} onChange={(e) =>onInputChange(e, i)} style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='midterm' style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='final' style={{ width: 45 + 'px' }} />
        </td>
        <td className='subTotalScore'></td>
        <td></td>
        <td className='grade'></td>
      </tr>
    );
  }

  let footerContent;
  if (numRows > 0) {
    const {attScore, assScore, midScore, finScore} = totalScores[`grade${numRows}`] || {};
    const calculatedScore = calScore(numRows);
    footerContent = (
      <tr>
        <td colSpan={3}>합계</td>
        <td className='sumCredit'></td>
        <td className='sumAttScore'>{attScore}</td>
        <td className='sumAssScore'>{assScore}</td>
        <td className='sumMidterm'>{midScore}</td>
        <td className='sumFinal'>{finScore}</td>
        <td className='totalScore'></td>
        <td className='avg'></td>
        <td className='finalGrade'></td>
      </tr>
    )
  }

  return (
    <table width={1000} border={1}>
      <thead style={{backgroundColor : '#00796B', color: '#ffffff'}}>
        <tr>
          <th>이수</th>
          <th>필수</th>
          <th>과목명</th>
          <th>학점</th>
          <th>출석점수</th>
          <th>과제점수</th>
          <th>중간고사</th>
          <th>기말고사</th>
          <th>총점</th>
          <th>평균</th>
          <th>성적</th>                                                                                                                      
        </tr>
      </thead>
      <tbody>
        {tableRows}
      </tbody>
      <tfoot>
        {footerContent}
      </tfoot>
    </table>
  );
}
export default App;
