import React, { useRef, useMemo } from "react";

function calculateGrade(score) {
  if (score >= 92) return "A+";
  if (score >= 91) return "A0";
  if (score >= 85) return "B+";
  if (score >= 80) return "B0";
  if (score >= 75) return "C+";
  if (score >= 70) return "C0";
  if (score >= 65) return "D+";
  if (score >= 60) return "D0";
  return "F";
}

function sortRows(a, b) {
  if (a.courseCategory !== b.courseCategory)
    return a.courseCategory.localeCompare(b.courseCategory);
  if (a.requirement !== b.requirement)
    return a.requirement.localeCompare(b.requirement);
  return a.courseName.localeCompare(b.courseName);
}

function TheTable({ gradeLevel, rows, onRowsChange }) {
  const rowID = useRef(rows.length > 0 ? Math.max(rows.map((row) => row.id)) + 1 : 0);

  const addRow = () => {
    const newRow = {
      id: rowID.current,
      courseCategory: "교양",
      requirement: "필수",
      courseName: "",
      credits: 0,
      attendanceScore: 0,
      assignmentScore: 0,
      midtermScore: 0,
      finalScore: 0,
      totalScore: 0,
      grade: "",
    };
    onRowsChange([...rows, newRow]);
    rowID.current++;
  };

  const deleteRow = (id) => {
    onRowsChange(rows.filter((row) => row.id !== id));
  };

  const updateRowValue = (id, key, value) => {
    onRowsChange(
      rows.map((row) =>
        row.id === id
          ? { ...row, [key]: key.includes("Score") || key === "credits" ? Number(value) : value }
          : row
      )
    );
  };

  const validateScore = (id, key, value, min, max) => {
    const numValue = Number(value);
    if (numValue < min || numValue > max) {
      alert(`${key}는 ${min} ~ ${max} 사이의 값만 입력 가능합니다.`);
      updateRowValue(id, key, "");
    } else {
      updateRowValue(id, key, numValue);
    }
  };

  const validateCourseName = (id, value) => {
    const isDuplicate = rows.some(
      (row) => row.courseName === value && row.id !== id && row.grade !== "F"
    );
    if (isDuplicate) {
      alert("이미 등록된 과목입니다. 다른 과목명을 입력해주세요.");
      updateRowValue(id, "courseName", "");
    } else {
      updateRowValue(id, "courseName", value);
    }
  };

  const saveRows = () => {
    const updatedRows = rows.map((row) => {
      if (row.grade === "") {
        const totalScore =
          row.attendanceScore +
          row.assignmentScore +
          row.midtermScore +
          row.finalScore;
        const grade = row.credits === 1 ? "P" : calculateGrade(totalScore);

        return { ...row, totalScore, grade };
      }
      return row;
    });
    onRowsChange([...updatedRows].sort(sortRows));
  };

  return (
    <div>
      <h2>{gradeLevel}학년</h2>
      <button onClick={addRow}>추가</button>
      <button onClick={saveRows}>저장</button>
      <table width={1000} border={1}>
        <thead style={{ backgroundColor: "#00796B", color: "#ffffff" }}>
          <tr>
            <th>이수</th>
            <th>필수</th>
            <th>과목명</th>
            <th>학점</th>
            <th>출석점수</th>
            <th>과제점수</th>
            <th>중간고사</th>
            <th>기말고사</th>
            <th>총점</th>
            <th>성적</th>
            <th>삭제</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row) => (
            <tr key={row.id}>
              <td>
                <select
                  value={row.courseCategory}
                  onChange={(e) =>
                    updateRowValue(row.id, "courseCategory", e.target.value)
                  }
                >
                  <option value="교양">교양</option>
                  <option value="전공">전공</option>
                </select>
              </td>
              <td>
                <select
                  value={row.requirement}
                  onChange={(e) =>
                    updateRowValue(row.id, "requirement", e.target.value)
                  }
                >
                  <option value="필수">필수</option>
                  <option value="선택">선택</option>
                </select>
              </td>
              <td>
                <input
                  type="text"
                  value={row.courseName}
                  onChange={(e) =>
                    validateCourseName(row.id, e.target.value)
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.credits}
                  onChange={(e) =>
                    updateRowValue(row.id, "credits", e.target.value)
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.attendanceScore}
                  onChange={(e) =>
                    validateScore(row.id, "attendanceScore", e.target.value, 0, 20)
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.assignmentScore}
                  onChange={(e) =>
                    validateScore(row.id, "assignmentScore", e.target.value, 0, 20)
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.midtermScore}
                  onChange={(e) =>
                    validateScore(row.id, "midtermScore", e.target.value, 0, 30)
                  }
                />
              </td>
              <td>
                <input
                  type="number"
                  value={row.finalScore}
                  onChange={(e) =>
                    validateScore(row.id, "finalScore", e.target.value, 0, 30)
                  }
                />
              </td>
              <td>{row.totalScore}</td>
              <td
                style={{ color: row.grade === "F" ? "red" : "inherit" }}
              >
                {row.grade}
              </td>
              <td>
                <button onClick={() => deleteRow(row.id)}>삭제</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TheTable;
