      ...updatedRowData[rowIndex],
      [fieldName]: value,
    }

    // 출석점수인 경우
    if (name === 'attScore') {

      // 입력 값이 조건에 맞지 않으면 알림창을 띄워주고 해당 입력값을 공백으로 설정하여 올바른 점수로 다시 입력할 수 있도록 설정
      if (value < 0 || value >= 30) {
        alert('출석 점수는 0보다 크고 30보다 작아야 합니다.');
        setAttScore('');
      } else {

        // 조건에 맞으면 현재 해당 값을 상태로 설정
        setAttScore(value);
      }

      // 과제점수인 경우 (위 주석과 동일한 구조)
    } else if (name === 'assScore') {
      if (value < 0 || value >= 30) {
        alert('과제 점수는 0보다 크고 30보다 작아야 합니다.');
        setAssScore('');
      } else {
        setAssScore(value);
      }
    }

  };

  const calSubTotalScore = (attScore, assScore, midterm, final) => {
    const sumScore = parseInt(attScore || 0) + parseInt(assScore || 0) + parseInt(midterm || 0) + parseInt(final || 0);
    return sumScore;
  }

  const calGrade = (attScore, assScore, midterm, final) => {
    const sumScore = calSubTotalScore(attScore, assScore, midterm, final);
    if (sumScore >= 92) {
      return 'A+';
    } else if (sumScore === 91) {
      return 'A0';
    } else if (sumScore >= 85) {
      return 'B+';
    } else if (sumScore >= 80) {
      return 'B0';
    } else if (sumScore >= 75) {
      return 'C+';
    } else if (sumScore >= 70) {
      return 'C0';
    } else if (sumScore >= 65) {
      return 'D+';
    } else if (sumScore >= 60) {
      return 'D0';
    } else {
      return 'F';
    }
  };

  const tableRows = [];

  for (let i = 0; i < numRows; i++) {

    tableRows.push(
      <tr key={i}>
        <td>
          <select>
            <option>교양</option>
            <option>전공</option>
          </select>
        </td>
        <td>
          <select>
            <option>필수</option>
            <option>선택</option>
          </select>
        </td>
        <td>
          <input type='text' name='subName' />
        </td>
        <td>
          <input type='text' name='credit' style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='attScore' value={attScore} onChange={onInputChange} style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='assScore' value={assScore} onChange={onInputChange} style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='midterm' style={{ width: 45 + 'px' }} />
        </td>
        <td>
          <input type='text' name='final' style={{ width: 45 + 'px' }} />
        </td>
        <td className='subTotalScore'>
          {calSubTotalScore(
            tableRows[i].props.children[4].props.value,
            tableRows[i].props.children[5].props.value,
            tableRows[i].props.children[6].props.value,
            tableRows[i].props.children[7].props.value
          )}
        </td>
        <td></td>
        <td className='grade'>
          {calGrade(
            tableRows[i].props.children[4].props.value,
            tableRows[i].props.children[5].props.value,
            tableRows[i].props.children[6].props.value,
            tableRows[i].props.children[7].props.value
          )}
        </td>
      </tr>
    );
  }

  let footerContent;
  if (numRows > 0) {
    footerContent = (
      <tr>
        <td colSpan={3}>합계</td>
        <td className='sumCredit'></td>
        <td className='sumAttScore'></td>
        <td className='sumAssScore'></td>
        <td className='sumMidterm'></td>
        <td className='sumFinal'></td>
        <td className='totalScore'></td>
        <td className='avg'></td>
        <td className='finalGrade'></td>
      </tr>
    )
  }

  return (
    <table width={1000} border={1}>
      <thead style={{backgroundColor : '#00796B', color: '#ffffff'}}>
        <tr>
          <th>이수</th>
          <th>필수</th>
          <th>과목명</th>
          <th>학점</th>
          <th>출석점수</th>
          <th>과제점수</th>
          <th>중간고사</th>
          <th>기말고사</th>
          <th>총점</th>
          <th>평균</th>
          <th>성적</th>                                                                                                                      
        </tr>
      </thead>
      <tbody>
        {tableRows}
      </tbody>
      <tfoot>
        {footerContent}
      </tfoot>
    </table>
  );
}
export default App;
